004|import { useEffect, useMemo, useState } from "react";
005|import { supabase } from "@/lib/supabase";
006|import { Button } from "@/components/ui/button";
007|import { Input } from "@/components/ui/input";
008|import { Label } from "@/components/ui/label";
009|import { toast } from "sonner";
010|import { Navigation } from "@/components/navigation";
011|
012|type CalRow = {
013|  date: string;        // DATE column as 'YYYY-MM-DD'
014|  day?: string | null; // optional
015|  working_time: number;
016|  over_time: number;
017|};
018|
019|function pad2(n: number) {
020|  return String(n).padStart(2, "0");
021|}
022|function monthRange(y: number, m1to12: number) {
023|  const from = `${y}-${pad2(m1to12)}-01`;
024|  const lastDay = new Date(y, m1to12, 0).getDate();
025|  const to = `${y}-${pad2(m1to12)}-${pad2(lastDay)}`;
026|  return { from, to };
027|}
028|
029|/** Try seeding a table for a month if RPC exists. Silently continue if RPC missing. */
030|async function trySeed(tableNo: 1 | 2, y: number, m1to12: number) {
031|  const fn = tableNo === 1 ? "calender1_seed_month" : "calender2_seed_month";
032|  const { error } = await supabase.rpc(fn as any, { p_year: y, p_month: m1to12 });
033|  if (error) {
034|    // If the RPC is missing / not defined, just ignore. Otherwise surface real errors.
035|    const msg = (error.message || "").toLowerCase();
036|    if (
037|      msg.includes("not exist") ||
038|      msg.includes("does not exist") ||
039|      msg.includes("no function") ||
040|      msg.includes("not found")
041|    ) {
042|      // Ignore missing seeder, continue to fetch rows.
043|      return;
044|    }
045|    throw new Error(`[${fn}] ${error.message}`);
046|  }
047|}
048|
049|/** Fetch a month's rows from a given calendar table */
050|async function fetchCalendar(table: "calender1" | "calender2", y: number, m1to12: number) {
051|  const { from, to } = monthRange(y, m1to12);
052|  const { data, error } = await supabase
053|    .from(table)
054|    .select("*")
055|    .gte("date", from)
056|    .lte("date", to)
057|    .order("date", { ascending: true });
058|
059|  if (error) throw new Error(`[${table}] ${error.message}`);
060|  return (data ?? []) as CalRow[];
061|}
062|
063|/** Inline editable numeric cell */
064|function EditableNumberCell({
065|  value,
066|  onSave,
067|  min = 0,
068|  step = 1,
069|  disabled,
070|  ariaLabel,
071|}: {
072|  value: number;
073|  onSave: (next: number) => Promise<void> | void;
074|  min?: number;
075|  step?: number;
076|  disabled?: boolean;
077|  ariaLabel?: string;
078|}) {
079|  const [text, setText] = useState(String(value));
080|  const [saving, setSaving] = useState(false);
081|
082|  useEffect(() => setText(String(value)), [value]);
083|
084|  const parse = (s: string) => {
085|    if (s.trim() === "") return null;
086|    const n = Number(s);
087|    if (!Number.isFinite(n)) return null;
088|    if (n < min) return min;
089|    return Math.round(n / step) * step;
090|  };
091|
092|  const commit = async () => {
093|    if (disabled || saving) return;
094|    const parsed = parse(text);
095|    if (parsed === null || parsed === value) {
096|      setText(String(value));
097|      return;
098|    }
099|    try {
100|      setSaving(true);
101|      await onSave(parsed);
102|      toast.success("Saved");
103|    } catch (e: any) {
104|      toast.error(e?.message ?? "Failed to save");
105|      setText(String(value));
106|    } finally {
107|      setSaving(false);
108|    }
109|  };
110|
111|  return (
112|    <div className="flex items-center justify-end gap-2">
113|      <Input
114|        aria-label={ariaLabel}
115|        type="number"
116|        inputMode="numeric"
117|        className="w-28 text-right"
118|        value={text}
119|        onChange={(e) => setText(e.target.value)}
120|        onBlur={commit}
121|        onKeyDown={(e) => {
122|          if (e.key === "Enter") (e.target as HTMLInputElement).blur();
123|          else if (e.key === "Escape") {
124|            setText(String(value));
125|            (e.target as HTMLInputElement).blur();
126|          }
127|        }}
128|        min={min}
