137|export default function CalendarPage() {
138|  const now = useMemo(() => new Date(), []);
139|  const [year, setYear] = useState(now.getFullYear());
140|  const [month, setMonth] = useState(now.getMonth() + 1); // 1..12
141|
142|  const [rows1, setRows1] = useState<CalRow[]>([]);
143|  const [rows2, setRows2] = useState<CalRow[]>([]);
144|  const [loading, setLoading] = useState(false);
145|
146|  async function load() {
147|    try {
148|      setLoading(true);
149|
150|      // Seed if RPCs exist (ignore if missing)
151|      await Promise.all([trySeed(1, year, month), trySeed(2, year, month)]);
152|
153|      // Fetch both tables
154|      const [d1, d2] = await Promise.all([
155|        fetchCalendar("calender1", year, month),
156|        fetchCalendar("calender2", year, month),
157|      ]);
158|      setRows1(d1);
159|      setRows2(d2);
160|    } catch (e: unknown) {
161|      const msg = e instanceof Error ? e.message : String(e);
162|      toast.error(`Failed to fetch records: ${msg}`);
163|    } finally {
164|      setLoading(false);
165|    }
166|  }
167|
168|  useEffect(() => {
169|    load();
170|    // eslint-disable-next-line react-hooks/exhaustive-deps
171|  }, [year, month]);
172|
173|  /** Update a single field in a specific table, optimistic */
174|  const saveField = async (
175|    table: "calender1" | "calender2",
176|    date: string,
177|    patch: Partial<Pick<CalRow, "working_time" | "over_time">>
178|  ) => {
179|    if (table === "calender1") {
180|      setRows1((prev) => prev.map((r) => (r.date === date ? { ...r, ...patch } : r)));
181|    } else {
182|      setRows2((prev) => prev.map((r) => (r.date === date ? { ...r, ...patch } : r)));
183|    }
184|
185|    const { error } = await supabase.from(table).update(patch).eq("date", date);
186|    if (error) throw new Error(error.message);
187|  };
188|
189|  /** Render a calendar table */
190|  const CalendarTable = ({
191|    title,
192|    tableName,
193|    rows,
194|  }: {
195|    title: string;
196|    tableName: "calender1" | "calender2";
197|    rows: CalRow[];
